mapper.select:
select zomore_commodity_category_id as zomoreCommodityCategoryId,zomore_commodity_category_parentid as zomoreCommodityCategoryParentid,zomore_commodity_category_name as zomoreCommodityCategoryName,zomore_commodity_category_priority as zomoreCommodityCategoryPriority,
zomore_commodity_isparent as zomoreCommodityIsparent,zomore_commodity_createtime as zomoreCommodityCreatetime, zomore_commodity_updatetime as zomoreCommodityUpdatetime,shop as shop
from zomore_commodity_category where shop=#{shop} and zomore_commodity_isparent=#{zomoreCommodityIsparent}
