mapper.select:
    select COUNT(zomore_commodity_sale_id) from zomore_commodity_sale
where zomore_commodity_sale_time BETWEEN #{startime} AND #{endtime} AND zomore_commodity_state ="1"
and zomore_commodity_shop =#{shop}

    select COUNT(zomore_commodity_sale_id) from zomore_service_commodityorder
where zomore_commodity_sale_time BETWEEN #{startime} AND #{endtime} AND zomore_commodity_state ="1"
and zomore_commodity_shop =#{shop}

  select count(member_number) from zomore_member where
  member_dateOfOpeningCard BETWEEN #{date}AND #{date1}
and member_shop = #{shop}

select zomore_commodity_code as code,zomore_commodity_name as name,zomore_commodity_store as store,zc.zomore_commodity_type as type
from zomore_commodity zc,zomore_commodity_store zcs
where zc.zomore_commodity_id = zcs.zomore_commodity_id
and  zc.zomore_commodity_shop =#{shop}
and  zc.zomore_commodity_type = #{selectBytype}
