mapper.select:
select chainset_sharedmember_Id as chainsetSharedmemberId,  chainset_sharedmember_name as chainsetSharedmemberName,
chainset_sharedmember_multipleId as chainsetSharedmemberMultipleId, chainset_sharedmember_level as chainsetSharedmemberLevel, chainset_sharedmember_state as chainsetSharedmemberState
from zomore_chainset_sharedmember
where chainset_sharedmember_multipleId=#{chainsetSharedmemberMultipleId}

    and chainset_sharedmember_level=#{chainsetSharedmemberLevel}


    and chainset_sharedmember_state=#{chainsetSharedmemberState}
