mapper.delete:
DELETE FROM zomore_commodity_category where zomore_commodity_category_name in ( SELECT a.name FROM (select zomore_commodity_category_name as name from zomore_commodity_category where shop=#{shop} GROUP BY zomore_commodity_category_name HAVING count(zomore_commodity_category_name) > 1 )a ) AND zomore_commodity_category_id NOT IN ( SELECT c.id FROM ( SELECT b.id as id FROM (select zomore_commodity_category_id as id,zomore_commodity_createtime as time,zomore_commodity_category_name as name FROM zomore_commodity_category where shop=#{shop}
ORDER BY time asc )b GROUP BY b.name
)c )
