mapper.select:
select c.commodityName as commodityName,c.commodityCode as commodityCode,c.commodityType as commodityType,SUM(c.saleNum) as saleNum,
SUM(c.commodityOutprice) as commodityOutprice, SUM(c.commodityRealMoney) as commodityRealMoney,c.shop as shop
from (select t.zomore_commodity_sale_id as id, d.zomore_commodity_sale_time as saletime,t.zomore_commodity_code as commodityCode,d.zomore_commodity_state as saleStatus,d.zomore_commodity_shop as shop,t.zomore_commodity_name as commodityName,t.zomore_commodity_type as commodityType,t.zomore_commodity_num
as saleNum, t.zomore_commodity_money as commodityOutprice, t.zomore_commodity_inmoney as commodityRealMoney
from zomore_commodity_sale_commodit  t LEFT JOIN zomore_commodity_sale d on t.zomore_commodity_sale_id=d.zomore_commodity_sale_id
)c where c.saleStatus=1

  and c.shop=#{shop}



 and c.saletime between #{saletime1} and #{saletime2}



  and c.commodityCode  LIKE concat(concat('%',#{commodityCode}),'%')


  and c.commodityName LIKE '%${commodityName}%'


  and c.commodityType =#{commodityType}

 GROUP BY commodityName

select c.commodityName as commodityName,c.commodityCode as commodityCode,c.commodityType as commodityType,SUM(c.saleNum) as saleNum, SUM(c.commodityOutprice) as commodityOutprice, SUM(c.commodityRealMoney) as commodityRealMoney,c.shop as shop from (select t.zomore_commodity_sale_id as id, d.zomore_commodity_sale_time as saletime,t.zomore_commodity_code as commodityCode,d.zomore_commodity_state as saleStatus,d.zomore_commodity_shop as shop,t.zomore_commodity_name as commodityName,t.zomore_commodity_type as commodityType,t.zomore_commodity_num as saleNum, t.zomore_commodity_money as commodityOutprice, t.zomore_commodity_inmoney as commodityRealMoney from zomore_commodity_sale_commodit t LEFT JOIN zomore_commodity_sale d on t.zomore_commodity_sale_id=d.zomore_commodity_sale_id )c where c.saleStatus=1 and c.shop in
#{item}
and c.saletime between #{startime} and #{endtime}
and c.commodityCode LIKE concat(concat('%',#{commodityCode}),'%')
and c.commodityName LIKE concat(concat('%',#{commodityName}),'%')
GROUP BY commodityName

select zomore_commodity_sale_id as zomoreCommoditySaleId, zomore_commodity_sale_time as zomoreCommoditySaleTime, zomore_commodity_sale_type as zomoreCommoditySaleType, zomore_commodity_sale_name as zomoreCommoditySaleName, zomore_commodity_sale_member as zomoreCommoditySaleMember, zomore_commodity_sale_num as zomoreCommoditySaleNum, zomore_commodity_sale_money as zomoreCommoditySaleMoney, zomore_commodity_sale_inmoney as zomoreCommoditySaleInmoney, zomore_commodity_sale_letmoney as zomoreCommoditySaleLetmoney, zomore_commodity_sale_savemoney as zomoreCommoditySaleSavemoney, zomore_commodity_sale_guide as zomoreCommoditySaleGuide, zomore_commodity_shop as zomoreCommodityShop, zomore_commodity_state as zomoreCommodityState, zomore_commodity_meno as zomoreCommodityMeno, zomore_commodity_guadanNum as zomoreCommodityGuadannum from zomore_commodity_sale where zomore_commodity_shop in
#{item}
and zomore_commodity_sale_time between #{startime}and #{endtime}
and zomore_commodity_sale_id LIKE concat(concat('%',#{num}),'%')
order by zomoreCommoditySaleTime desc
