zomore_commodity_transfer_outstore as zomoreCommodityTransferOutstore,zomore_commodity_transfer_meno as zomoreCommodityTransferMeno,zomore_commodity_transfer_time as zomoreCommodityTransferTime ,zomore_commodity_transfer_datetime as zomoreCommodityTransferDatetime,
state as state  from zomore_commodity_transfer where zomore_commodity_transfer_instore =#{shop} or zomore_commodity_transfer_outstore=#{shop}

select zomore_commodity_transfer_id as zomoreCommodityTransferId,zomore_commodity_transfer_num as zomoreCommodityTransferNum,zomore_commodity_transfer_size as zomoreCommodityTransferSize,zomore_commodity_transfer_instore as zomoreCommodityTransferInstore,
 zomore_commodity_transfer_outstore as zomoreCommodityTransferOutstore,zomore_commodity_transfer_meno as zomoreCommodityTransferMeno,zomore_commodity_transfer_time as zomoreCommodityTransferTime ,zomore_commodity_transfer_datetime as zomoreCommodityTransferDatetime,
 state as state  from zomore_commodity_transfer where zomore_commodity_transfer_instore =#{shop}  or zomore_commodity_transfer_outstore=#{shop} and zomore_commodity_transfer_id = #{transferCommodityId}

select zomore_commodity_transfer_id as zomoreCommodityTransferId,zomore_commodity_transfer_num as zomoreCommodityTransferNum,zomore_commodity_transfer_size as zomoreCommodityTransferSize,zomore_commodity_transfer_instore as zomoreCommodityTransferInstore, zomore_commodity_transfer_outstore as zomoreCommodityTransferOutstore,zomore_commodity_transfer_meno as zomoreCommodityTransferMeno,zomore_commodity_transfer_time as zomoreCommodityTransferTime ,zomore_commodity_transfer_datetime as zomoreCommodityTransferDatetime, state as state from zomore_commodity_transfer where zomore_commodity_transfer_instore =#{shop} or zomore_commodity_transfer_outstore=#{shop}
and zomore_commodity_transfer_id like concat(concat('%',#{num}),'%')
order by zomoreCommodityTransferDatetime desc

