mapper.select:
select shopping_guide_id as shoppingGuideId, shopping_guide_name as shoppingGuideName, shopping_guide_phone as shoppingGuidePhone, shopping_guide_image as shoppingGuideImage, shopping_guide_sale as shoppingGuideSale, shopping_guide_appoint as shoppingGuideAppoint, shopping_guide_recharge as shoppingGuideRecharge, shopping_guide_subcard as shoppingGuideSubcard, shopping_guide_shoppingcar as shoppingGuideShoppingcar, shopping_guide_lab as shoppingGuideLab, shopping_guide_ismember as shoppingGuideIsmember, shopping_guide_cashier_store as shoppingGuideCashierStore, shopping_guide_role as shoppingGuideRole, shopping_guide_password as shoppingGuidePassword, shopping_guide_state as shoppingGuideState, shopping_guide_meno as shoppingGuideMeno,createtime as createtime, updatetime as updatetime from zomore_shopping_guide where shopping_guide_cashier_store in
#{item}
and shopping_guide_name like concat(concat('%',#{name}),'%')
and shopping_guide_state = #{state}
order by createtime desc
